mapper.update:
UPDATE book
   SET reviewyn = 'Y'
 WHERE bidx = #{bidx}

UPDATE review
   SET title = #{title},
   	   content = #{content},
   	   rate = #{rate}
 WHERE rvidx = #{rvidx}

mapper.delete:
DELETE FROM image
      WHERE originfilename = #{originFileName}
        AND rvidx = #{rvidx}

