mapper.update:
UPDATE user_plend
   SET userPhone = #{userPhone}
   	  ,email = #{email}
   	  ,addr = #{addr}
   	  ,birth = #{birth}
   	  ,password = #{password}
 WHERE uidx = #{uidx}

UPDATE user_plend 
   SET delyn = 'Y'
 WHERE uidx = #{uidx}

UPDATE book
   SET delyn = 'Y'
 WHERE bidx = #{bidx}

